    private void btnLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnLoginActionPerformed
        // TODO add your handling code here:
        String uid = userID.getText();
        String pw = new String(password.getPassword());
        if (!uid.matches("U[0-9][0-9][0-9][0-9]")) {
            JOptionPane.showMessageDialog(this, "Wrong format of UserID: U0000");
            userID.setText("");
            password.setText("");
        } else {
            User us = User.login(uid, pw);
            if (us != null) {
                LibraryGUI lg = ((LibraryGUI) (this.getParent().getParent().getParent().getParent().getParent()));
                lg.setUser(us);
                if (lg.getUser().getUserRight() == 1) {
                    JOptionPane.showMessageDialog(this, "Welcome user " + us.getFullName());
                    lg.turnOnTask();
                } else if (lg.getUser().getUserRight() == 3) {
                    JOptionPane.showMessageDialog(this, "Welcome admin " + us.getFullName());
                    lg.turnOnAdminTask();
                    
                }
            } else {
                JOptionPane.showMessageDialog(this, "Login failed!");
            }

        }
    }//GEN-LAST:event_btnLoginActionPerformed

